import React from 'react'

import Card from 'react-bootstrap/Card'
import { Row, Col, Button } from 'react-bootstrap';

import './Crd.css';

export default function Crd(props) {
  const colr = ['Success', 'Secondary'];
  const stat=props.stat;
  const variant=colr[0];
  if (stat) {
    const variant = colr[0];
  } else {
    const variant = colr[1];
  }
  console.log(props.stat);
  return (
    <>
      <Card
        bg={variant.toLowerCase()}
        key={variant}
        text={variant.toLowerCase() === 'light' ? 'dark' : 'white'}
        // style={{ minWidth: '18rem' }}
        className="mb-2">
        <Card.Body>
          <Card.Title className='crd-Title'>{props.task}</Card.Title>
          <center>
            <Row>
              <Col>
                <Button variant="info" onClick={props.onDone}>Set Done</Button>
              </Col>
              <Col>
                <Button variant="danger" onClick={props.onDelete}>Delete</Button>
              </Col>
            </Row>
          </center>
        </Card.Body>
      </Card>
    </>
  )
}
