import { React, useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'

import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'

import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import Swal from 'sweetalert2/dist/sweetalert2.js';

import UserNav from '../Component/UserPage/UserNav'

import './UserHome.css';
import Crd from '../Component/UserPage/Crd'

function UserHome() {

    const email = localStorage.getItem("mail");

    // const urlUser = "http://localhost:5000/api/users/login";
    const urlTask = "http://localhost:5000/api/task/"+email;

    const navigate = useNavigate();
    const [data, setData] = useState({
        task: "",
    });
    const [task, settask] = useState([]);
    useEffect(() => {
        // const url = urlTask + email;
        axios.get(urlTask)
            .then(res => {
                settask(res.data);
                // console.log(res.data)
            })
            .catch(err => console.log(err))
    }, []);

    return (
        <>
            <UserNav />
            <Container fluid className='bg-dark justify-content-md-center text-light bg'>
                <h1>Hi, Jhone Wick</h1>
                <Row className="justify-content-md-center">
                    <Col xs="12" sm="12" md="4" xl="4" xxl="3">
                        <Form className='bg-login'>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Control type="text" placeholder="Enter Task Here" />
                                <span className='txt-span'>We'll never share your Data with anyone else.</span>
                            </Form.Group>
                            <center>
                                <Button variant="outline-success" size="sm" className='btn-2'>Add</Button>
                            </center>
                            {/* <div className="d-grid gap-1">
                            </div> */}
                        </Form>
                        <Row className="justify-content-md-center">
                            <Col>
                                <Crd title={"Gggg"} type={"1"} id={"1"} />
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </Container>
        </>
    )
}

export default UserHome
