import { React, useState } from 'react'
import { Container, Row, Col } from 'react-bootstrap'

import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'

import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import Swal from 'sweetalert2/dist/sweetalert2.js';

import UserNav from '../Component/UserPage/UserNav'

import './UserHome.css';
import Crd from '../Component/UserPage/Crd'

function UserHome() {

    const urlUser = "http://localhost:5000/api/users/login";
    const urlToDo = "http://localhost:5000/api/todos/";
    const navigate = useNavigate();
    const [data, setData] = useState({
        email: "",
    })
    function submit(e) {
        e.preventDefault();
        const token = localStorage.getItem("token");

        const api = axios.create({
            baseURL: "http://localhost:5000/api",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}` // Attach token here
            }
        });
        const result = axios.post(url, {
            email: data.email,
            password: data.password
        }).then(
            res => {
                console.log(res.data)

                const token = res.data.token;
                const usermail = data.email;

                // Save to Local Storage
                localStorage.setItem("token", token);
                localStorage.setItem("mail", usermail);


            }
        ).catch(err => {
            Swal.fire({
                title: 'Something went wrong',
                text: err,
                icon: 'error'
            })
        })


    }
    function handle(e) {
        const newData = { ...data }
        newData[e.target.id] = e.target.value
        setData(newData)
        console.log(newData)
    }

