import React from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'

import { useNavigate } from 'react-router-dom'

import axios from 'axios'

import Swal from 'sweetalert2/dist/sweetalert2.js';

import './Login.css';
import HomeNav from '../Component/Home/HomeNav';

function Login() {
    const url = "http://localhost:5000/api/users/login";
    const navigate = useNavigate();
    const [data, setData] = useState({
        name: "",
        email: "",
        password: "",
        password2: "",
    })
    function submit(e) {
        e.preventDefault();
        if (data.password == data.password2) {
            const result = axios.post(url, {
                name: data.name,
                email: data.email,
                password: data.password
            }).then(
                res => {
                    console.log(res.data)
                    Swal.fire({
                        title: 'Account Created',
                        text: res.data.message,
                        icon: 'success'
                    })
                    navigate('/login');

                }
            ).catch(err => {
                Swal.fire({
                    title: 'Something went wrong',
                    text: err,
                    icon: 'error'
                })
            })
        }else{
            Swal.fire({
                title: 'Password Error',
                text: "Password Fields Missmatched !",
                icon: 'error'
            })
        }

    }
    function handle(e) {
        const newData = { ...data }
        newData[e.target.id] = e.target.value
        setData(newData)
        console.log(newData)
    }
    return (
        <>
            <HomeNav />
            <Container fluid className='bg-dark justify-content-md-center text-light bg'>
                <Row className="justify-content-md-center">
                    <Col xs="12" sm="12" md="4" xl="4" xxl="3">

                        <h1>Login</h1>
                        <Form className='bg-login'>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>Email address</Form.Label>
                                <Form.Control type="email" placeholder="Enter email" />
                                <span className='txt-span'>We'll never share your Data with anyone else.</span>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder="Password" />
                                <span className='txt-span'>We'll never share your Data with anyone else.</span>
                            </Form.Group>
                            <Row>
                                <Col>
                                    <center>
                                        <a href="/userPage">
                                            <Button variant="primary" size="sm" className='btn-2'>
                                                Login
                                            </Button>
                                        </a>
                                    </center>
                                </Col>
                                <Col>
                                    <center>
                                        <a href="/createacc">
                                            <Button variant="secondary" size="sm" className='btn-2'>
                                                Create Account
                                            </Button>
                                        </a>
                                    </center>
                                </Col>
                            </Row>
                            {/* <div className="d-grid gap-1">
                            </div> */}
                        </Form>
                    </Col>
                </Row>

            </Container>

        </>
    )
}

export default Login
